    isAuthenticating.set(true);

    options ||= {};
    return request(WEB_APP_BIOMETRY_REQUEST_AUTH, 'biometry_auth_requested', {
      ...options,
      params: { reason: (options.reason || '').trim() },
    })
      .then(response => {
