import { parseThemeParams } from './parseThemeParams.js';
import type { GetCssVarNameFn } from './types.js';

type StorageValue = ThemeParams;

const STORAGE_KEY = 'themeParams';
const THEME_CHANGED_EVENT = 'theme_changed';

/**
 * Creates CSS variables connected with the current theme parameters.
 *
 * By default, created CSS variables names are following the pattern "--tg-theme-{name}", where
 * {name} is a theme parameters key name converted from camel case to kebab case.
 *
 * Default variables:
 * - `--tg-theme-bg-color`
 * - `--tg-theme-secondary-text-color`
 *
 * Variables are being automatically updated if theme parameters were changed.
 *
 * @param getCSSVarName - function, returning complete CSS variable name for the specified
 * theme parameters key.
 * @returns Function to stop updating variables.
 * @throws TypedError ERR_ALREADY_CALLED
 */
export const bindCssVars = withIsMounted((getCSSVarName?: GetCssVarNameFn): VoidFunction => {
  if (isCssVarsBound()) {
    throw new TypedError(ERR_ALREADY_CALLED);
  }
  getCSSVarName ||= (prop) => `--tg-theme-${camelToKebab(prop)}`;

  function forEachEntry(fn: (key: string, value: RGB) => void): void {
    Object.entries(state()).forEach(([k, v]) => {
      v && fn(k, v);
    });
  }

  function actualize(): void {
    forEachEntry((k, v) => {
      setCssVar(getCSSVarName!(k), v);
    });
  }

  actualize();
  state.sub(actualize);
  isCssVarsBound.set(true);

  return () => {
    forEachEntry(deleteCssVar);
    state.unsub(actualize);
    isCssVarsBound.set(false);
  };
}, isMounted);

/**
 * Mounts the component.
 *
 * This function restores the component state and is automatically saving it in the local storage
 * if it changed.
 */
export function mount(): void {
  if (!isMounted()) {
    on(THEME_CHANGED_EVENT, onThemeChanged);
    state.set(isPageReload() && getStorageValue<StorageValue>(STORAGE_KEY) || retrieveLaunchParams().themeParams);
    isMounted.set(true);
  }
}

/**
 * Actualizes the theme parameters whenever an according event was received.
 * @param e - event data.
 */
const onThemeChanged: EventListener<'theme_changed'> = (e) => {
  const value = parseThemeParams(e.theme_params);
  state.set(value);
  setStorageValue<StorageValue>(STORAGE_KEY, value);
};

/**
 * Unmounts the component, removing the listener, saving the component state in the local storage.
 */
export function unmount(): void {
  off(THEME_CHANGED_EVENT, onThemeChanged);
  isMounted.set(false);
}
import {
  MethodNameWithVersionedParams,
  MethodVersionedParams,
  supports, TypedError,
} from '@telegram-apps/bridge';
import { $version } from '@/scopes/globals.js';
import { ERR_NOT_SUPPORTED } from '@/errors.js';

type HasCheckSupportMethodTuple<FnArgs extends any[]> = {
  [M in MethodNameWithVersionedParams]: [
    method: M,
    parameter: MethodVersionedParams<M>,
    shouldCheck: (...args: FnArgs) => boolean,
  ]
}[MethodNameWithVersionedParams];

export type WithSupports<
  F extends (...args: any) => any,
  S extends Record<string, unknown>
> = F & {
  /**
   * @returns True, if this function is supported.
   */
  supports(method: keyof S): boolean;
};

/**
 * Adds the "supports" method to the passed function returning true, if specified action is
 * supported by the current Mini Apps version.
 *
 * Wrapped function throws an error in case, the passed arguments are subject to check for one
 * of the schema properties.
 * @param fn - function to extend.
 * @param schema - map where key is an action name and value is a tuple containing Mini Apps
 * method name and its option.
 */

/*@__NO_SIDE_EFFECTS__*/
export function withSupports<
  F extends (...args: any) => any,
  S extends Record<string, HasCheckSupportMethodTuple<Parameters<F>>>,
>(fn: F, schema: S): WithSupports<F, S> {
  function sup(param: keyof S): boolean {
    const tuple = schema[param];
    return supports(tuple[0], tuple[1], $version());
  }

  return Object.assign(
    (...args: Parameters<F>): ReturnType<F> => {
      for (const k in schema) {
        if (schema[k][2](...args) && !sup(k)) {
          throw new TypedError(ERR_NOT_SUPPORTED, `Parameter "${k}" is not supported`);
        }
      }
      return fn(...args);
    },
    fn,
    { supports: sup },
  );
}
