import { type GetCSSVarNameFn, request } from './static.js';
import {
  state,
  mountError,
  isMounted,
  isCssVarsBound,
  isMounting,
} from './signals.js';
import type { State } from './types.js';

interface StorageValue {
  height: number;
  isExpanded: boolean;
  stableHeight: number;
  width: number;
}

/**
 * Creates CSS variables connected with the current viewport.
 *
 * By default, created CSS variables names are following the pattern "--tg-theme-{name}", where
 * {name} is a theme parameters key name converted from camel case to kebab case.
 *
 * Default variables:
 * - `--tg-viewport-height`
 * - `--tg-viewport-width`
 * - `--tg-viewport-stable-height`
 *
 * Variables are being automatically updated if viewport was changed.
 *
 * @param getCSSVarName - function, returning complete CSS variable name for the specified
 * viewport property.
 * @returns Function to stop updating variables.
 * @throws {TypedError} ERR_ALREADY_CALLED
 * @throws {TypedError} ERR_NOT_MOUNTED
 */
export const bindCssVars = withIsMounted((getCSSVarName?: GetCSSVarNameFn): VoidFunction => {
  if (isCssVarsBound()) {
    throw new TypedError(ERR_ALREADY_CALLED);
  }
  getCSSVarName ||= (prop) => `--tg-viewport-${camelToKebab(prop)}`;
  const props = ['height', 'width', 'stableHeight'] as const;

  function actualize(): void {
    props.forEach(prop => {
      setCssVar(getCSSVarName!(prop), `${state()[prop]}px`);
    });
  }

  actualize();
  state.sub(actualize);
  isCssVarsBound.set(true);

  return () => {
    props.forEach(deleteCssVar);
    state.unsub(actualize);
    isCssVarsBound.set(false);
  };
}, isMounted);

/**
 * A method that expands the Mini App to the maximum available height. To find out if the Mini
 * App is expanded to the maximum height, refer to the value of the `isExpanded`.
 * @see isExpanded
 */
export function expand(): void {
  postEvent('web_app_expand');
}

function formatState(state: State): State {
  return {
    isExpanded: state.isExpanded,
    height: truncate(state.height),
    width: truncate(state.width),
    stableHeight: truncate(state.stableHeight),
  };
}

/**
 * Mounts the component.
 *
 * This function restores the component state and is automatically saving it in the local storage
 * if it changed.
 */
export const mount = createMountFn<State>(
  options => {
    // Try to restore the state using the storage.
    const s = isPageReload() && getStorageValue<StorageValue>('viewport');
    if (s) {
      return s;
    }

    // If the platform has a stable viewport, it means we could use the window global object
    // properties.
    if ([
      'macos',
      'tdesktop',
      'unigram',
      'webk',
      'weba',
      'web',
    ].includes(retrieveLaunchParams().platform)) {
      const w = window;
      return {
        isExpanded: true,
        height: w.innerHeight,
        width: w.innerWidth,
        stableHeight: w.innerHeight,
      };
    }

    // We were unable to retrieve data locally. In this case, we are sending a request returning
    // the viewport information.
    options.timeout ||= 1000;
    return request(options).then(data => ({
      height: data.height,
      isExpanded: data.isExpanded,
      stableHeight: data.isStable ? data.height : state().stableHeight,
      width: data.width,
    }));
  },
  result => {
    on('viewport_changed', onViewportChanged);
    subAndCall(state, onStateChanged);
    state.set(formatState(result));
  },
  { isMounted, isMounting, mountError },
);

const onViewportChanged: EventListener<'viewport_changed'> = (data) => {
  state.set(formatState({
    height: data.height,
    width: data.width,
    isExpanded: data.is_expanded,
    stableHeight: data.is_state_stable ? data.height : state().stableHeight,
  }));
};

function onStateChanged(): void {
  setStorageValue<StorageValue>('viewport', state());
}

/**
 * Formats value to make it stay in bounds [0, +Inf).
 * @param value - value to format.
 */
function truncate(value: number): number {
  return Math.max(value, 0);
}

/**
 * Unmounts the component, removing the listener, saving the component state in the local storage.
 */
export function unmount(): void {
  off('viewport_changed', onViewportChanged);
  state.unsub(onStateChanged);
}
import { supports, TypedError, type OpenLinkBrowser } from '@telegram-apps/bridge';
import { createSafeURL } from '@telegram-apps/navigation';

import { $version, postEvent } from '@/scopes/globals.js';
import { ERR_INVALID_HOSTNAME } from '@/errors.js';

export interface OpenLinkOptions {
  /**
   * Attempts to use the instant view mode.
   */
  tryInstantView?: boolean;
  /**
   * A preferred browser to open the link in.
   */
  tryBrowser?: OpenLinkBrowser;
}

const OPEN_TG_LINK_METHOD = 'web_app_open_tg_link';

/**
 * Opens a link.
 *
 * The Mini App will not be closed.
 *
 * Note that this method can be called only in response to the user
 * interaction with the Mini App interface (e.g. click inside the Mini App or on the main button).
 * @param url - URL to be opened.
 * @param options - additional options.
 */
export function openLink(url: string, options?: OpenLinkOptions): void {
  options ||= {};
  postEvent('web_app_open_link', {
    url: createSafeURL(url).toString(),
    try_browser: options.tryBrowser,
    try_instant_view: options.tryInstantView,
  });
}

/**
 * Opens a Telegram link inside Telegram app. The Mini App will be closed. It expects passing
 * links in full format, with hostname "t.me".
 * @param url - URL to be opened.
 * @throws {TypedError} ERR_INVALID_HOSTNAME
 */
export function openTelegramLink(url: string): void {
  const { hostname, pathname, search } = new URL(url, 'https://t.me');
  if (hostname !== 't.me') {
    throw new TypedError(ERR_INVALID_HOSTNAME);
  }

  if (!supports(OPEN_TG_LINK_METHOD, $version())) {
    window.location.href = url;
    return;
  }

  postEvent(OPEN_TG_LINK_METHOD, { path_full: pathname + search });
}

/**
 * Shares specified URL with the passed to the chats, selected by user. After being called,
 * it closes the mini application.
 *
 * This method uses Telegram's Share Links.
 * @param url - URL to share.
 * @param text - text to append after the URL.
 * @see https://core.telegram.org/api/links#share-links
 * @see https://core.telegram.org/widgets/share#custom-buttons
 */
export function shareURL(url: string, text?: string): void {
  openTelegramLink(
    `https://t.me/share/url?` + new URLSearchParams({ url, text: text || '' })
      .toString()
      // By default, URL search params encode spaces with "+".
      // We are replacing them with "%20", because plus symbols are working incorrectly
      // in Telegram.
      .replace(/\+/g, '%20'),
  );
}
import {
  TypedError,
  CancelablePromise,
  sleep,
  type PhoneRequestedStatus,
  type WriteAccessRequestedStatus,
  type ExecuteWithOptions,
} from '@telegram-apps/bridge';
import { searchParams, object, number, string, date } from '@telegram-apps/transformers';

import { invokeCustomMethod, request } from '@/scopes/globals.js';
import { withIsSupported } from '@/scopes/toolkit/withIsSupported.js';
import { ERR_ACCESS_DENIED, ERR_ALREADY_CALLED } from '@/errors.js';
import { signal } from '@telegram-apps/signals';

/**
 * Requested contact information.
 */
export interface RequestedContact {
  contact: {
    userId: number;
    phoneNumber: string;
    firstName: string;
    lastName?: string;
  };
  authDate: Date;
  hash: string;
}

const WEB_APP_REQUEST_PHONE = 'web_app_request_phone';
const WEB_APP_REQUEST_WRITE_ACCESS = 'web_app_request_write_access';

/**
 * True if phone access is currently being requested.
 */
export const isRequestingPhoneAccess = signal(false);

/**
 * True if write access is currently being requested.
 */
export const isRequestingWriteAccess = signal(false);

/**
 * Attempts to get requested contact.
 * @param options - execution options.
 * @throws {TypedError} ERR_CUSTOM_METHOD_ERR_RESPONSE
 */
function getRequestedContact(options?: ExecuteWithOptions): CancelablePromise<RequestedContact> {
  options ||= {};
  return invokeCustomMethod('getRequestedContact', {}, {
    ...options,
    timeout: options.timeout || 5000,
  })
    .then(
      searchParams({
        contact: object({
          userId: ['user_id', number()],
          phoneNumber: ['phone_number', string()],
          firstName: ['first_name', string()],
          lastName: ['last_name', string(true)],
        })(),
        authDate: ['auth_date', date()],
        hash: string(),
      })(),
    );
}

/**
 * Requests current user contact information. In contrary to requestPhoneAccess, this method
 * returns promise with contact information that rejects in case, user denied access, or request
 * failed.
 * @param options - additional options.
 * @throws {TypedError} ERR_ACCESS_DENIED
 * @throws {TypedError} ERR_CUSTOM_METHOD_ERR_RESPONSE
 * @throws {TypedError} ERR_NOT_SUPPORTED
 */
export const requestContact = withIsSupported(
  (options?: ExecuteWithOptions): CancelablePromise<RequestedContact> => {
    return new CancelablePromise(
      async (res, _, abortSignal) => {
        const asyncOptions = {
          postEvent: (options || {}).postEvent,
          abortSignal,
        };

        // First of all, let's try to get the requested contact.
        // Probably, we already requested it before.
        try {
          return res(await getRequestedContact(asyncOptions));
        } catch {
        }

        // Then, request access to the user's phone.
        const status = await requestPhoneAccess(asyncOptions);
        if (status !== 'sent') {
          throw new TypedError(ERR_ACCESS_DENIED);
        }

        // Time to wait before executing the next request.
        let sleepTime = 50;

        // We are trying to retrieve the requested contact until the deadline was reached.
        while (!abortSignal.aborted) {
          try {
            return res(await getRequestedContact(asyncOptions));
          } catch {
          }

          // Sleep for some time.
          await sleep(sleepTime);

          // Increase the sleep time not to kill the backend service.
          sleepTime += 50;
        }
      }, options,
    );
  },
  WEB_APP_REQUEST_PHONE,
);

/**
 * Requests current user phone access. Method returns promise, which resolves
 * status of the request. In case, user accepted the request, Mini App bot will receive
 * the according notification.
 *
 * To obtain the retrieved information instead, utilize the `requestContact` method.
 * @param options - additional options.
 * @see requestContact
 * @throws {TypedError} ERR_ALREADY_CALLED
 * @throws {TypedError} ERR_NOT_SUPPORTED
 */
export const requestPhoneAccess = withIsSupported(
  (options?: ExecuteWithOptions): Promise<PhoneRequestedStatus> => {
    if (isRequestingPhoneAccess()) {
      throw new TypedError(ERR_ALREADY_CALLED);
    }
    isRequestingPhoneAccess.set(true);

    return request(WEB_APP_REQUEST_PHONE, 'phone_requested', options)
      .then(r => r.status)
      .finally(() => {
        isRequestingPhoneAccess.set(false);
      });
  }, WEB_APP_REQUEST_PHONE,
);

/**
 * Requests write message access to the current user.
 * @param options - additional options.
 * @throws {TypedError} ERR_ALREADY_CALLED
 * @throws {TypedError} ERR_NOT_SUPPORTED
 */
export const requestWriteAccess = withIsSupported(
  (options?: ExecuteWithOptions): Promise<WriteAccessRequestedStatus> => {
    if (isRequestingWriteAccess()) {
      throw new TypedError(ERR_ALREADY_CALLED);
    }
    isRequestingWriteAccess.set(true);

    return request(WEB_APP_REQUEST_WRITE_ACCESS, 'write_access_requested', options)
      .then(r => r.status)
      .finally(() => {
        isRequestingWriteAccess.set(false);
      });
  }, WEB_APP_REQUEST_WRITE_ACCESS,
);
import {
  captureSameReq,
  retrieveLaunchParams,
  CancelablePromise,
  TypedError,
  type ExecuteWithOptions,
  type SwitchInlineQueryChatType,
  type ExecuteWithPostEvent, supports,
} from '@telegram-apps/bridge';

import { postEvent, createRequestId, request, $version } from '@/scopes/globals.js';
import { withIsSupported } from '@/scopes/toolkit/withIsSupported.js';
import { ERR_DATA_INVALID_SIZE } from '@/errors.js';

const WEB_APP_READ_TEXT_FROM_CLIPBOARD = 'web_app_read_text_from_clipboard';
const WEB_APP_SWITCH_INLINE_QUERY = 'web_app_switch_inline_query';
const WEB_APP_SHARE_TO_STORY = 'web_app_share_to_story';

export interface ShareStoryOptions extends ExecuteWithPostEvent {
  /**
   * The caption to be added to the media.
   * 0-200 characters for regular users and 0-2048 characters for premium subscribers.
   * @see https://telegram.org/faq_premium#telegram-premium
   */
  text?: string;
  /**
   * An object that describes a widget link to be included in the story.
   * Note that only premium subscribers can post stories with links.
   * @see https://telegram.org/faq_premium#telegram-premium
   */
  widgetLink?: {
    /**
     * The URL to be included in the story.
     */
    url: string;
    /**
     * The name to be displayed for the widget link, 0-48 characters.
     */
    name?: string;
  };
}

/**
 * Reads a text from the clipboard and returns a string or null. null is returned
 * in cases:
 * - A value in the clipboard is not a text.
 * - Access to the clipboard is not granted.
 * @throws {TypedError} ERR_NOT_SUPPORTED
 */
export const readTextFromClipboard = withIsSupported(
  (options?: ExecuteWithOptions): CancelablePromise<string | null> => {
    const reqId = createRequestId();

    return request(WEB_APP_READ_TEXT_FROM_CLIPBOARD, 'clipboard_text_received', {
      ...options,
      params: { req_id: reqId },
      capture: captureSameReq(reqId),
    }).then(({ data = null }) => data);
  }, WEB_APP_READ_TEXT_FROM_CLIPBOARD,
);

/**
 * A method used to send data to the bot.
 *
 * When this method called, a service message sent to the bot containing the data of the length
 * up to 4096 bytes, and the Mini App closed.
 *
 * See the field `web_app_data` in the class [Message](https://core.telegram.org/bots/api#message).
 *
 * This method is only available for Mini Apps launched via a Keyboard button.
 * @param data - data to send to bot.
 * @throws {TypedError} ERR_DATA_INVALID_SIZE
 */
export function sendData(data: string): void {
  const { size } = new Blob([data]);
  if (!size || size > 4096) {
    throw new TypedError(ERR_DATA_INVALID_SIZE);
  }
  postEvent('web_app_data_send', { data });
}

/**
 * A method that opens the native story editor.
 * @throws {TypedError} ERR_NOT_SUPPORTED
 */
export const shareStory = withIsSupported(
  (mediaUrl: string, options?: ShareStoryOptions) => {
    options ||= {};
    (options.postEvent || postEvent)(WEB_APP_SHARE_TO_STORY, {
      text: options.text,
      media_url: mediaUrl,
      widget_link: options.widgetLink,
    });
  }, WEB_APP_SHARE_TO_STORY,
);

/**
 * Inserts the bot's username and the specified inline query in the current chat's input field.
 * Query may be empty, in which case only the bot's username will be inserted. The client prompts
 * the user to choose a specific chat, then opens that chat and inserts the bot's username and
 * the specified inline query in the input field.
 * @param query - text which should be inserted in the input after the current bot name. Max
 * length is 256 symbols.
 * @param chatTypes - List of chat types which could be chosen to send the message. Could be
 * empty list.
 * @throws {TypedError} ERR_NOT_SUPPORTED
 */
export const switchInlineQuery = withIsSupported(
  (query: string, chatTypes?: SwitchInlineQueryChatType[]) => {
    postEvent(WEB_APP_SWITCH_INLINE_QUERY, {
      query: query,
      chat_types: chatTypes || [],
    });
  },
  () => {
    return supports(WEB_APP_SWITCH_INLINE_QUERY, $version()) && !!retrieveLaunchParams().botInline;
  }
);
/**
 * @returns True, if current environment is server.
 */
export function isSSR(): boolean {
  return typeof window === 'undefined';
}
import {
  createCbCollector,
  defineEventHandlers,
  on,
  removeEventHandlers,
} from '@telegram-apps/bridge';

import { postEvent, configure, type ConfigureOptions } from '@/scopes/globals.js';

export interface InitOptions extends ConfigureOptions {
  /**
   * True if SDK should accept styles sent from the Telegram application.
   * @default true
   */
  acceptCustomStyles?: boolean;
}

/**
 * Initializes the SDK allowing it to properly handle events, sent from the native Telegram
 * application.
 * @param options - function options.
 * @returns A function, to perform a cleanup.
 */
export function init(options?: InitOptions): VoidFunction {
  // Configure the package global dependencies.
  configure(options);

  // Define event handlers, so we could receive the events from the Telegram application.
  defineEventHandlers();

  const [addCleanup, cleanup] = createCbCollector(
    on('reload_iframe', () => {
      postEvent('iframe_will_reload');
      window.location.reload();
    }),
    removeEventHandlers,
  );

  const { acceptCustomStyles = true } = options || {};
  if (acceptCustomStyles) {
    const style = document.createElement('style');
    style.id = 'telegram-custom-styles';
    document.head.appendChild(style);

    addCleanup(
      on('set_custom_style', html => {
        // It is safe to use innerHTML here as long as style tag has a special behavior related
        // to the specified content.
        // In case any script will be passed here, it will not be executed, so XSS is not possible.
        style.innerHTML = html;
      }),
      () => {
        document.head.removeChild(style);
      },
    );
  }

  // Notify Telegram that the application is ready.
  //
  // This will result in sending style tag html content from the Telegram web application.
  // We should call this method also to start receiving "reload_iframe" events from the Telegram
  // application.
  //
  // It really has no effect outside non-Telegram web environment.
  postEvent('iframe_ready', { reload_supported: true });

  return cleanup;
}
