import React, { useState } from "react";
import "./Catalog.css";

const dishes = [
  { id: 1, name: "Бурурброт", price: 10, calories: 250 },
  { id: 2, name: "Плов", price: 12, calories: 300 },
  { id: 3, name: "Шошлек", price: 15, calories: 400 },
  { id: 4, name: "Марожка", price: 20, calories: 500 },
  { id: 5, name: "Картошка", price: 17, calories: 200 },
];

function Catalog() {
  const [cart, setCart] = useState({});

  const updateQuantity = (id, change) => {
    setCart((prev) => {
      const newQuantity = (prev[id] || 0) + change;
      if (newQuantity <= 0) {
        const updatedCart = { ...prev };
        delete updatedCart[id]; 
        return updatedCart;
      }
      return { ...prev, [id]: newQuantity };
    });
  };

  const totalPrice = Object.keys(cart).reduce(
    (sum, id) => sum + (cart[id] || 0) * dishes.find((dish) => dish.id === Number(id)).price,
    0
  );

  return (
    <div className="catalog-container">
      <div className="top-buttons">
        <button className="small-button">Удалить сессию</button>
        <button className="small-button">Код комнаты</button>
        <button className="small-button">Официант</button>
        <button className="small-button">Чекаут гоу</button>
      </div>

      <h2>Каталог еще обновляется
      </h2>

      <div className="menu-grid">
        {dishes.map((dish) => (
          <div key={dish.id} className="menu-item">
            <div className="dish-img">Фотка жоского блюда</div>
            <div className="dish-info">
              <p className="dish-price"><strong>{dish.price} рублей</strong></p>
              <p className="dish-name">{dish.name}</p>
              <p className="dish-calories">{dish.calories} грамм</p>
            </div>
            <div className="quantity-controls">
                {cart[dish.id] ? (
                  <>
                    <button onClick={() => updateQuantity(dish.id, -1)}>-</button>
                    <span><strong>{cart[dish.id]}</strong></span>
                    <button onClick={() => updateQuantity(dish.id, 1)}>+</button>
                  </>
                ) : (
                  <button className="add-button" onClick={() => updateQuantity(dish.id, 1)}>+</button>
                )}
              </div>
          </div>
        ))}
      </div>

      <div className="total-price">
        <p>Итого: <strong>{totalPrice} рублей</strong></p>
        <button className="checkout-button">Далее   </button>
      </div>
    </div>
  );
}

export default Catalog;
