import React, { useState } from "react";
import "./Catalog.css";

const dishes = [
  { id: 1, name: "Dish 1", price: 10, calories: 250 },
  { id: 2, name: "Dish 2", price: 12, calories: 300 },
  { id: 3, name: "Dish 3", price: 15, calories: 400 },
  { id: 4, name: "Dish 4", price: 20, calories: 500 },
];

function Catalog() {
  const [cart, setCart] = useState({});

  const updateQuantity = (id, change) => {
    setCart((prev) => {
      const newQuantity = (prev[id] || 0) + change;
      return newQuantity > 0 ? { ...prev, [id]: newQuantity } : { ...prev, [id]: 0 };
    });
  };

  const totalPrice = Object.keys(cart).reduce(
    (sum, id) => sum + (cart[id] || 0) * dishes.find((dish) => dish.id === Number(id)).price,
    0
  );

  return (
    <div className="catalog-container">
      {/* Верхние кнопки */}
      <div className="top-buttons">
        <button className="small-btn">delete_session</button>
        <button className="small-btn">room_code</button>
        <button className="small-btn">call_waiter</button>
        <button className="small-btn">go_checkout</button>
      </div>

      <h2>Catalog</h2>

      {/* Блок с блюдами */}
      <div className="menu-grid">
        {dishes.map((dish) => (
          <div key={dish.id} className="menu-item">
            <div className="dish-img">Picture</div>
            <div className="dish-info">
              <p className="dish-price">${dish.price}</p>
              <p className="dish-name">{dish.name}</p>
              <p className="dish-calories">{dish.calories} calories</p>
            </div>
            <div className="quantity-controls">
              <button onClick={() => updateQuantity(dish.id, -1)}>-</button>
              <span>{cart[dish.id] || 0}</span>
              <button onClick={() => updateQuantity(dish.id, 1)}>+</button>
            </div>
          </div>
        ))}
      </div>

      {/* Итоговая сумма */}
      <div className="total-price">
        <p>Personal Total Price: <strong>${totalPrice}</strong></p>
        <button className="checkout-btn">go_cart</button>
      </div>
    </div>
  );
}

export default Catalog;
